def convert(data):
    data2 = []
    ltime = 0
    for d in data:
        ctime = ltime + d[0]
        d[0] = ctime
        data2.append(d)
        ltime = ctime
    return data2

def plot(data, position):
    i = 0
    x = []
    y = []
    while (i < len(data)):
        if (i > 0):
            x.append(data[i][0]-1)
            y.append(data[i-1][1] + position)
        x.append(data[i][0])
        y.append(data[i][1] + position)
        i = i + 1

    ax.plot(x,y)

